mapper.select:
 		SELECT cno, p.pimg, p.pname, cnt, (p.price * cnt), c.mno
		FROM cart AS c, product AS p, userinfo AS u
		WHERE c.mno = u.mno AND c.pno = p.pno AND c.mno = #{mno}

SELECT (p.price * cnt)
FROM cart AS c, product AS p, userinfo AS u
WHERE c.mno = u.mno AND c.pno = p.pno AND c.mno = #{mno}

mapper.delete:
DELETE FROM cart WHERE cno IN
#{cno}
ORDER BY cno ASC
